glm::mat4 Ruby::getWorldTransform(flecs::entity e) {
    glm::mat4 mat = e.get<Transform3d>()->value;
    auto parent = e.parent();
    if(parent && parent.has<Transform3d>()) {
        mat *= Ruby::getWorldTransform(parent);
    }
    return mat;
}
